import React from 'react'

const QuotesItem = (props) =>{
    const {id,name,body,removeItem} =props
    return (
        <div>
            <blockquote>{ body } - { name }</blockquote>
            <button> edit </button>
            <button onClick={() =>{
                const confirmRemove = window.confirm("Are you sure?")
                if(confirmRemove){
                    removeItem(id)
                }                
            }}> remove </button>
        </div>
    )
}
export default QuotesItem
