import React, {useState} from 'react'
import {v4 as uuidv4} from 'uuid'

const QuotesForm = (props) =>{
    const {formSubmission,id ,name:author,body:quote} = props
    const[name,setName]=useState( author ? author : '')
    const[body,setBody]=useState(quote ?quote:'')

    const handleSubmit = (e) =>{
        e.preventDefault()
        const formData ={
            //id:Number(new Date()),
            id:uuidv4(),
            name:name,
            body:body
        }
            formSubmission(formData)
            //reset form
            setName('')
            setBody('')
    }
    const handleNameChange = (e) =>{
        setName(e.target.value)
    }
    const handleBodyChange = (e) =>{
        setBody(e.target.value)
    }
    return(
        <div>
            <h2>Add Quote</h2>
            <form onSubmit={handleSubmit}>
                <label>Name</label><br/>
                <input type="text" value={name} onChange={handleNameChange}/><br/>
                <label>Body</label><br/>
                <textarea value={body} onChange={handleBodyChange}></textarea><br/>
                <input type="submit" value="save"/>
            </form>
        </div>
    )
}
export default QuotesForm
